const Runtime = require("../../engine/runtime");

const ArgumentType = require("../../extension-support/argument-type");
const BlockType = require("../../extension-support/block-type");
const Clone = require("../../util/clone");
const Cast = require("../../util/cast");
const formatMessage = require("format-message");
const Video = require("../../io/video");

const blockIconURI =
    "data:image/png;base64,";
const _songs = ["happy", "sad", "sleeping", "angry", "up", "down", "victory"];
const _colors = [
    "red",
    "green",
    "blue",
    "yellow",
    "cyan",
    "magenta",
    "white",
    "random",
];
const _icons = [
    "heart",
    "check",
    "X",
    "smile",
    "frown",
    "ghost",
    "triangle",
    "diamond",
    "square",
    "checkers",
    "note",
];
const _drive = ["forward", "backward"];
const _turn = ["left", "right"];
const _button = ["A", "B", "A or B", "A and B", "neither A nor B"];
const _line_states = ["right side", "left side", "neither side", "both sides"];
const EXTENSION_ID = "microbitRobot";

// Core, Team, and Official extension classes should be registered statically with the Extension Manager.
// See: scratch-vm/src/extension-support/extension-manager.js
class MicrobitRobot {
    constructor(runtime) {
        /**
         * Store this for later communication with the Scratch VM runtime.
         * If this extension is running in a sandbox then `runtime` is an async proxy object.
         * @type {Runtime}
         */
        this.scratch_vm = runtime;
        this.scratch_vm.registerPeripheralExtension(EXTENSION_ID, this);
        this.scratch_vm.connectPeripheral(EXTENSION_ID, 0);

        this.robot = this;

        this._mStatus = 1;
        this._mConnection = null;
        this._mConnectionTimeout = null;
        this.CHROME_EXTENSION_ID = "jpehlabbcdkiocalmhikacglppfenoeo"; // APP ID on Chrome Web Store

        this.msg1 = {};
        this.msg2 = {};
        this.dist_read = 0;
        this.a_button = 0;
        this.b_button = 0;
        this.left_line = 0;
        this.right_line = 0;
        this.last_reading = 0;

        this.scratch_vm.on("PROJECT_STOP_ALL", this.resetRobot.bind(this));
        this.scratch_vm.on(
            "CONNECT_MICROBIT_ROBOT",
            this.connectToExtension.bind(this)
        );
    }

    /**
     * @return {object} This extension's metadata.
     */
    getInfo() {
        return {
            id: EXTENSION_ID,
            name: formatMessage({
                id: "microbitRobot",
                default: "PRG Microbit Robot Blocks",
                description:
                    "Extension using ESPCam Chrome extension to communicate with Microbit robot",
            }),
            showStatusButton: true,
            blockIconURI: blockIconURI,
            menuIconURI: blockIconURI,

            blocks: [
                {
                    func: "CONNECT_MICROBIT_ROBOT",
                    blockType: BlockType.BUTTON,
                    text: "Connect Robot",
                },
                "---",
                {
                    opcode: "playMusic",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.playMusic",
                        default: "play song [SONG]",
                        description: "Play song using the piezo",
                    }),
                    arguments: {
                        SONG: {
                            type: ArgumentType.STRING,
                            menu: "SONGS",
                            defaultValue: _songs[0],
                        },
                    },
                },
                "---",
                {
                    opcode: "setRgbLedColor",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.setLEDColor",
                        default: "set headlight color [COLOR]",
                        description: "Set the RGB headlight color",
                    }),
                    arguments: {
                        COLOR: {
                            type: ArgumentType.STRING,
                            menu: "COLORS",
                            defaultValue: _colors[0],
                        },
                    },
                },
                {
                    opcode: "rgbLedOff",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.ledOff",
                        default: "turn headlights off",
                        description: "Turn off the LED",
                    }),
                    arguments: {},
                },
                "---",
                {
                    opcode: "setLEDDisplay",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.setLEDDisplay",
                        default: "set LED display [ICON]",
                        description: "Set the LED display to an icon",
                    }),
                    arguments: {
                        ICON: {
                            type: ArgumentType.STRING,
                            menu: "ICONS",
                            defaultValue: _icons[0],
                        },
                    },
                },
                {
                    opcode: "ledDisplayOff",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.ledDisplayOff",
                        default: "turn LED display off",
                        description: "Turn off the LED display",
                    }),
                    arguments: {},
                },
                "---",
                {
                    opcode: "drive",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.driveForwardBackward",
                        default: "drive [DIR] for [NUM] seconds",
                        description:
                            "Send command to robot to drive forward or backward",
                    }),
                    arguments: {
                        NUM: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 1,
                        },
                        DIR: {
                            type: ArgumentType.String,
                            menu: "DIRS",
                            defaultValue: _drive[0],
                        },
                    },
                },
                {
                    opcode: "turn",
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: "arduinoBot.turnRightLeft",
                        default: "turn [TURN] for [NUM] seconds",
                        description:
                            "Send command to robot to turn right or left",
                    }),
                    arguments: {
                        NUM: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 1,
                        },
                        TURN: {
                            type: ArgumentType.String,
                            menu: "TURNS",
                            defaultValue: _turn[0],
                        },
                    },
                },
                "---",
                {
                    opcode: "whenButtonPressed",
                    text: formatMessage({
                        id: "arduinoBot.readButtonStatus",
                        default: "when [BUTTON] button pressed",
                        description:
                            "Trigger when buttons on microbit are pressed",
                    }),
                    blockType: BlockType.HAT,
                    arguments: {
                        BUTTON: {
                            type: ArgumentType.String,
                            menu: "BUTTON_STATES",
                            defaultValue: _button[0],
                        },
                    },
                },
                {
                    opcode: "readLineStatus",
                    blockType: BlockType.BOOLEAN,
                    text: formatMessage({
                        id: "arduinoBot.readLineSensorStatus",
                        default: "line detected on [LINE]",
                        description: "detect line sensor state",
                    }),
                    arguments: {
                        LINE: {
                            type: ArgumentType.String,
                            menu: "LINE_STATES",
                            defaultValue: _line_states[0],
                        },
                    },
                },
                {
                    opcode: "readDistance",
                    blockType: BlockType.REPORTER,
                    text: formatMessage({
                        id: "arduinoBot.readDistance",
                        default: "read distance",
                        description:
                            "Get distance read from ultrasonic distance sensor",
                    }),
                    arguments: {},
                },
                // play sounds
                // add blocks for speech?
            ],
            menus: {
                SONGS: {
                    acceptReporters: false,
                    items: _songs,
                },
                COLORS: {
                    acceptReporters: false,
                    items: _colors,
                },
                ICONS: {
                    acceptReporters: false,
                    items: _icons,
                },
                DIRS: {
                    acceptReporters: false,
                    items: _drive,
                },
                TURNS: {
                    acceptReporters: false,
                    items: _turn,
                },
                BUTTON_STATES: {
                    acceptReporters: false,
                    items: _button,
                },
                LINE_STATES: {
                    acceptReporters: false,
                    items: _line_states,
                },
            },
        };
    }
    isConnected() {
        //console.log("isConnected status: " + this._mStatus);
        return this._mStatus == 2;
    }
    scan() {
        console.log("microbitRobotScan");
        this.connectToExtension();
    }
    connect() {}
    disconnect() {
        console.log("Closing extension");
        chrome.runtime.sendMessage(this.CHROME_EXTENSION_ID, { close: true });
    }
    disconnectedFromExtension() {
        this._mStatus = 1;
        var msg = {};
        console.log("Lost connection to robot");
        this.scratch_vm.emit(
            this.scratch_vm.constructor.PERIPHERAL_DISCONNECTED
        );
        msg.status = "disconnected";
        if (this._mConnection != null) this._mConnection.postMessage(msg);
    }
    connectToExtension() {
        // Can probably do this without Chrome extension
        // VID is a Vendor ID. For the micro:bit this is 0x0d28, PID is a Product ID. For the micro:bit this is 0x0204
        // navigator.getUSBDevices https://developers.google.com/web/updates/2016/03/access-usb-devices-on-the-web
        // https://developers.google.com/web/updates/2015/07/interact-with-ble-devices-on-the-web

        // Save reference to robot for use later

        var robot = this;
        var boundMsgHandler = this.onMsgFromExtension.bind(this);

        // Attenpt to connect to the Gizmo Chrome Extension
        chrome.runtime.sendMessage(
            this.CHROME_EXTENSION_ID,
            { launch: true },
            function (response) {
                if (response === undefined) {
                    //Chrome app not found
                    // Must have the wrong extension ID (if extension was not downloaded from Chrome webstore, the extension id is not consistent)
                    console.log(
                        "Chrome app not found with extension ID: " +
                            robot.CHROME_EXTENSION_ID
                    );

                    // Attempt to get the extension ID from local browser storage
                    robot.CHROME_EXTENSION_ID = window.localStorage.getItem(
                        "espcam_extension_id"
                    );
                    console.log(
                        "Stored extension ID: " + robot.CHROME_EXTENSION_ID
                    );
                    if (
                        robot.CHROME_EXTENSION_ID === undefined ||
                        robot.CHROME_EXTENSION_ID === "" ||
                        robot.CHROME_EXTENSION_ID === null
                    ) {
                        // If there is no extension ID in local browser storage, prompt user to enter one
                        robot.CHROME_EXTENSION_ID = window.prompt(
                            "Enter the correct Chrome Extension ID",
                            "jpehlabbcdkiocalmhikacglppfenoeo"
                        );
                    }
                    robot._mStatus = 0;
                    // Try to connect to the Chrome extension again
                    if (robot.CHROME_EXTENSION_ID === null) {
                        // user must have hit cancel
                        robot.CHROME_EXTENSION_ID =
                            "jpehlabbcdkiocalmhikacglppfenoeo"; // molfimodiodghknifkeikkldkogpapki
                    } else {
                        robot.connectToExtension();
                    }
                } else if (response.status === false) {
                    //Chrome app says not connected
                    console.log("Chome extension is not running"); // what does this mean?
                    robot._mStatus = 1;
                } else {
                    // Chrome app is connected
                    console.log("Chrome extension found");
                    // Save the extension ID in local browser storage for next time
                    window.localStorage.setItem(
                        "espcam_extension_id",
                        robot.CHROME_EXTENSION_ID
                    );
                    if (robot._mStatus !== 2) {
                        robot._mConnection = chrome.runtime.connect(
                            robot.CHROME_EXTENSION_ID
                        );
                        // Add listener that triggers onMsgFromExtension everytime the Chrome extension gets a message from the robot
                        robot._mConnection.onMessage.addListener(
                            boundMsgHandler
                        );
                        // We're not sure that it's working until we start receiving messages
                        robot._mStatus = 1;
                    }
                }
            }
        );
    }

    /**
     * Implement onMsgFromExtension
     * @msg {chrome.runtime.Message} the message received from the connected Chrome extension
     * When a message is received from the Chrome extension, and therefore the robot, this handles that message
     */
    onMsgFromExtension(msg) {
        if (this._mStatus == 1) {
            console.log("Receiving messages from robot");
            console.log(msg);
        }

        this._mStatus = 2;
        this.scratch_vm.emit(this.scratch_vm.constructor.PERIPHERAL_CONNECTED);
        var buffer = msg.buffer;
        var imageCapture = msg.capture;

        if (buffer != undefined && buffer != null) {
            // The beginning of the buffer (from firmata) starts with 224, if this buffer starts with 224 it is the beginning of the message
            if (buffer[0] == 224) {
                this.messageParser(buffer);
                this.last_reading = 0; // Last reading signifies that the last thing stored in the msg buffer is the first part of the message
            }

            if (buffer[0] != 224 && this.last_reading == 0) {
                // Checking last reading makes sure that we don't concatenate the wrong part of the message
                this.messageParser(buffer);
                this.last_reading = 1;
            }
        } else if (imageCapture != undefined && imageCapture != null) {
        }
        // Detect if the robot gets disconnected
        if (this._mConnectionTimeout != null)
            clearTimeout(this._mConnectionTimeout);
        this._mConnectionTimeout = setTimeout(
            this.disconnectedFromExtension.bind(this),
            1000
        );
    }

    /**
     * Implement messageParser
     * @buf {byte buffer} a buffer containing a series of opcode keys and data value pairs
     * @dist_read {int} the last reading from the ultrasonic distance sensor
     * @msg1 {byte buffer} since the entire buffer does not always get transmitted in a message, this will store the first part of the buffer
     * @msg2 {byte buffer} since the entire buffer does not always get transmitted in a message, this will store the second part of the buffer
     */
    messageParser(buf) {
        var msg = {};
        if (buf[0] == 224) {
            // RANDI these 200 numbers are arbitrary and could be changed here and in the microbit code
            this.msg1 = buf;
        } else if (buf[0] != 224) {
            this.msg2 = buf;
        }
        msg.buffer = this.msg1.concat(this.msg2);

        if (msg.buffer.length > 10) {
            msg.buffer = msg.buffer.slice(0, 10); // The length of the buffer (from firmata) is only 10 bytes
        }
        if (msg.buffer.length == 10) {
            if (msg.buffer[0] == 224) {
                // RANDI these 200 numbers are arbitrary and could be changed here and in the microbit code
                this.a_button = Math.round(msg.buffer[1]);
            }
            if (msg.buffer[2] == 237) {
                this.b_button = Math.round(msg.buffer[3]);
            }
            if (msg.buffer[4] == 238) {
                this.left_line = Math.round(msg.buffer[5]);
            }
            if (msg.buffer[6] == 239) {
                this.right_line = Math.round(msg.buffer[7]);
            }
            if (msg.buffer[8] == 240) {
                // The opcode key before the ultrasonic distance reading data is 240
                this.dist_read = Math.round(msg.buffer[9]);
            }
        }
    }
    resetRobot() {
        this.stopMotors();
        this.rgbLedOff();
        this.stopMusic();
        this.ledDisplayOff();
    }

    /**
     *
     */
    playMusic(args) {
        console.log("play song: " + args.SONG);

        // Translate color to index
        var idxStr = (_songs.indexOf(args.SONG) + 1).toString(16).charCodeAt(0);

        // Send message
        var msg = {};
        msg.buffer = [77, idxStr, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);
    }

    stopMusic(args) {
        console.log("Music off");
        var msg = {};
        msg.buffer = [77, 48, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);

        return;
    }

    /**
     *
     */
    setRgbLedColor(args) {
        console.log("set LED color: " + args.COLOR);

        // Translate color to index
        var idxStr = (_colors.indexOf(args.COLOR) + 1)
            .toString(16)
            .charCodeAt(0);

        // Send message
        var msg = {};
        msg.buffer = [76, idxStr, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);
    }
    rgbLedOff() {
        console.log("Headlights off");
        var msg = {};
        msg.buffer = [76, 48, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);

        return;
    }

    /**
     *
     */
    setLEDDisplay(args) {
        console.log("set LED display: " + args.ICON);

        // Translate color to index
        var idxStr = (_icons.indexOf(args.ICON) + 1).toString(16).charCodeAt(0);
        // Send message
        var msg = {};
        msg.buffer = [83, idxStr, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);
    }
    ledDisplayOff() {
        console.log("LED display off");
        var msg = {};
        msg.buffer = [83, 48, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);

        return;
    }

    /**
     * Implement readDistance
     * @returns {string} the distance, in cm, of the nearest object. -1 means error
     */
    readDistance() {
        var distance = this.dist_read;
        if (distance == 0) {
            distance = -1;
        }
        return distance;
    }

    /**
     * Implement readButtonStaus
     * @returns {string} t
     */
    readButtonStatus(args) {
        var state = args.BUTTON;

        if (state == "A") {
            return this.a_button == 1;
        } else if (state == "B") {
            return this.b_button == 1;
        } else if (state == "A or B") {
            return this.a_button == 1 || this.b_button == 1;
        } else if (state == "A and B") {
            return this.a_button == 1 && this.b_button == 1;
        } else if (state == "neither A nor B") {
            return this.a_button == 0 && this.b_button == 0;
        }
        return false; // should never get here
    }
    /**
     * Implement whenButtonPressed
     */
    whenButtonPressed(args) {
        return this.readButtonStatus(args);
    }

    /**
     * Implement readLineStatus
     * @returns {string} t
     */
    readLineStatus(args) {
        var state = args.LINE;

        if (state == "right side") {
            return this.right_line == 1;
        } else if (state == "left side") {
            return this.left_line == 1;
        } else if (state == "both sides") {
            return this.right_line == 1 && this.left_line == 1;
        } else if (state == "neither side") {
            return this.right_line == 0 && this.left_line == 0;
        }
        return false; // should never get here
    }

    stopMotors() {
        var msg = {};
        console.log("Sending D0 to stop servos");
        msg.buffer = [68, 48, 10];
        if (this._mConnection != null) this._mConnection.postMessage(msg);
    }

    /**
     * Implement drive to drive forward or backward
     * @secs {number} the number of seconds to drive backward
     * @dir {string} whether to turn "left" or "right"
     * @callback {function} the code to call when this function is done executing
     */
    drive(args) {
        var msg = {};
        var secs = args.NUM;
        var dir = args.DIR;

        if (dir == "forward") {
            console.log("Sending D1 to drive forward, secs: " + secs);
            msg.buffer = [68, 49, 10];
        } else {
            console.log("Sending D2 to drive backward, secs: " + secs);
            msg.buffer = [68, 50, 10];
        }
        if (this._mConnection != null) this._mConnection.postMessage(msg);

        return new Promise((resolve) => {
            setTimeout(() => {
                this.stopMotors();
                resolve();
            }, secs * 1000);
        });
    }

    /**
     * Implement turn to turn left or right
     * @secs {number} the number of seconds to turn left
     * @dir {string} whether to turn "left" or "right"
     * @callback {function} the code to call when this function is done executing
     */
    turn(args) {
        var msg = {};
        var secs = args.NUM;
        var dir = args.TURN;

        if (dir == "left") {
            console.log("Sending D3 to turn left, secs: " + secs);
            msg.buffer = [68, 51, 10];
        } else {
            console.log("Sending D4 to turn right, secs: " + secs);
            msg.buffer = [68, 52, 10];
        }

        if (this._mConnection != null) this._mConnection.postMessage(msg);

        return new Promise((resolve) => {
            setTimeout(() => {
                this.stopMotors();
                resolve();
            }, secs * 1000);
        });
    }
}
module.exports = MicrobitRobot;
